#ifdef LOCAL_DEBUG
#include "defines.h"

#endif

#define mkp make_pair
#define map obstacleGrid

class Solution {
public:
    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid)
    {
        int m = obstacleGrid.size();
        int n = obstacleGrid[0].size();
        if (map[0][0] == 1 || map[m - 1][n - 1] == 1)
            return 0;
        int dp[n] = { 0 };
        queue<pair<int, int>> q;
        q.push(mkp(0, 0));
        map[0][0] = 2;
        while (!q.empty()) {
            int x = q.front().first;
            int y = q.front().second;
            q.pop();
            // if (x > 0 && map[x - 1][y] == 0) {
            //     q.push(mkp(x - 1, y));
            //     obstacleGrid[x - 1][y] = 2;
            // }
            if (x < m - 1 && map[x + 1][y] == 0) {
                q.push(mkp(x + 1, y));
                obstacleGrid[x + 1][y] = 2;
            }
            // if (y > 0 && map[x][y - 1] == 0) {
            //     q.push(mkp(x, y - 1));
            //     obstacleGrid[x][y - 1] = 2;
            // }
            if (y < n - 1 && map[x][y + 1] == 0) {
                q.push(mkp(x, y + 1));
                obstacleGrid[x][y + 1] = 2;
            }
        }
        dp[n - 1] = 1;
        // for (int i = 0; i < m; i++) {
        //     for (int j = 0; j < n; j++)
        //         cout << map[i][j] << ' ';
        //     cout << endl;
        // } 
        for (int i = m - 1; i >= 0; i--) {
            dp[n - 1] = dp[n - 1] == 1 && obstacleGrid[i][n - 1] == 2;
            for (int j = n - 2; j >= 0; j--) {
                if (obstacleGrid[i][j] != 2) {
                    dp[j] = 0;
                } else {
                    // if (dp[j] > 718991952 || dp[j + 1] > 718991952)
                    //     cout << i << ' '<< j << endl;
                    dp[j] += dp[j + 1];
                }
            }
        }
        return dp[0];
    }
};

